const configurationName = env.get('Name');
const sheetConfig = env.get('Sheets Definition');
const fileName = env.get('File Name');

// Create/Update Schema
global.set(
    String("Configurations.XLSX." + configurationName),
    {
        sheetConfig: sheetConfig,
        fileName: fileName
    });

return msg;

// Many of the results contain whitespace. We want to trim this for the file.

// Convert the raw query into a dataForge DataFrame
const df = new dataForge.DataFrame(msg.payload);

// Use map to iterate over every row of the dataset. Then use forEach on the keys (column names) in each row, if the type of the row is a string, perform a trim
const trimmedDf = df.map(row => {
    Object.keys(row).forEach(key => {
        if (typeof row[key] === 'string') {
            row[key] = row[key].trim();
        }
    });
    return row;
});

global.set(msg.configuration.sheetConfig.sheetDf, trimmedDf.serialize())

msg.payload = trimmedDf;
msg.dataFrame = trimmedDf.serialize();

return msg;

// Load the configuration
//  We need to deep copy the configuration using a dynamic path.
//  Change node allows dynamic path but only for shallow copy
//  which causes the downstream split node to modify the configuration stored in Global Context

// Load the configuration
const configurationPath = "Configurations.XLSX"
const configurationName = env.get("Configuration")
const configuration = global.get([configurationPath, configurationName].join("."));

// Check if configuration exists
if (typeof configuration === 'undefined') {
    node.warn(configurationName + ' at ' + configurationPath + ' not found!');
    return [null, msg];
}

// Make a Deep Copy of the configuration
msg.configuration = JSON.parse(JSON.stringify(configuration));

return [msg, null];

const configurationName = env.get('Configuration')

// load the configuration
const config = global.get('Configurations.XLSX.' + configurationName);

// Check if configuration exists
if (typeof config === 'undefined') {
    node.error("XLSX configuration: " +  configurationName + ' not found in global context: Configurations.XLSX');
    return [null, msg];
}

// Create a workbook to insert sheets into
const workbook = XLSX.utils.book_new();

// Create Sheet
config.sheetConfig.forEach(config => {
    const sheetDf = global.get(config.sheetDf);
    const sheetData = (typeof sheetDf !== 'undefined') ? sheetDf.values : []
    const sheetColumns = config.columns
    const sheet = XLSX.utils.json_to_sheet(sheetData, { header: sheetColumns });
    XLSX.utils.book_append_sheet(workbook, sheet, config.sheetName);
})

// Save the workbook
const fileDir = global.get("Configurations.FileFolder");
XLSX.writeFile(workbook, fileDir + "/" + config.fileName + ".xlsx");

return [msg, null];

